import org.apache.hadoop.yarn.util.resource.ResourceUsage;
import org.junit.Test;

import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicBoolean;

import static org.junit.Assert.*;

public class FairOrderingPolicyTest {

    @Test
    public void testGetSchedulingResourceUsageConcurrency() throws InterruptedException {
        // Create a test instance
        FairOrderingPolicy policy = new FairOrderingPolicy();

        // Setup test conditions
        final int threadCount = 2;
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch endLatch = new CountDownLatch(threadCount);
        final AtomicBoolean deadlockDetected = new AtomicBoolean(false);

        // Create threads that will call the method concurrently
        for (int i = 0; i < threadCount; i++) {
            new Thread(() -> {
                try {
                    startLatch.await();
                    policy.getSchedulingResourceUsage();
                } catch (InterruptedException e) {
                    deadlockDetected.set(true);
                } finally {
                    endLatch.countDown();
                }
            }).start();
        }

        // Start all threads at once
        startLatch.countDown();

        // Wait for threads to finish with timeout
        boolean completed = endLatch.await(2, java.util.concurrent.TimeUnit.SECONDS);

        // Test will fail on buggy version due to deadlock from synchronized method
        // Test will pass on fixed version as threads can complete concurrently
        assertFalse("Deadlock detected in synchronized version", deadlockDetected.get());
        assertTrue("Threads did not complete in time - potential deadlock", completed);
    }
}

