import org.junit.Test;
import java.util.regex.Pattern;
import java.util.regex.Matcher;
import static org.junit.Assert.*;

public class UmaskParserTest {

    @Test
    public void testOctalPatternWithLeadingZeros() {
        // This test should pass with the fixed pattern but fail with the buggy one
        String[] validInputs = {
            "0777",
            "0077",
            "0007",
            " 0777 ",
            "+0777",
            "+0077",
            "0",
            "00",
            "000",
            "0000"
        };

        // Test with fixed pattern (should pass)
        Pattern fixedPattern = Pattern.compile("^\\s*[+]?(0*)([0-7]{3})\\s*$");
        for (String input : validInputs) {
            Matcher m = fixedPattern.matcher(input);
            assertTrue("Input '" + input + "' should match with fixed pattern", m.matches());
        }

        // Test with buggy pattern (should fail for some cases)
        Pattern buggyPattern = Pattern.compile("^\\s*[+]?()([0-7]{3})\\s*$");
        for (String input : validInputs) {
            Matcher m = buggyPattern.matcher(input);
            if (input.length() > 3) {
                assertFalse("Input '" + input + "' should NOT match with buggy pattern", m.matches());
            }
        }
    }

    @Test
    public void testSpecificFourDigitCase() {
        // This specifically tests the 4-digit case that was failing
        String fourDigitInput = "0007";

        // Fixed pattern should match
        Pattern fixedPattern = Pattern.compile("^\\s*[+]?(0*)([0-7]{3})\\s*$");
        assertTrue(fixedPattern.matcher(fourDigitInput).matches());

        // Buggy pattern should not match
        Pattern buggyPattern = Pattern.compile("^\\s*[+]?()([0-7]{3})\\s*$");
        assertFalse(buggyPattern.matcher(fourDigitInput).matches());
    }
}

