    @Test
    public void testTurkishLocaleCaseConversion() {
        // Turkish has special case conversion rules where 'i' becomes '' (dotted I)
        // when converted to uppercase with Turkish locale
        Locale defaultLocale = Locale.getDefault();
        try {
            // Set Turkish locale to demonstrate the difference
            Locale.setDefault(new Locale("tr", "TR"));

            String testString = "TITLE_CASE_i";

            // Test that US locale produces consistent case conversion regardless of system locale
            String usConverted = testString.toLowerCase(Locale.US);
            assertFalse("Should not contain dotted i in US locale", 
                         usConverted.contains(""));  // Turkish lowercase i

            // This would fail with buggy code (Locale.ENGLISH) on Turkish system locale
            String englishConverted = testString.toLowerCase(Locale.ENGLISH);
            assertFalse("Should not contain dotted i in ENGLISH locale", 
                        englishConverted.contains(""));

            // The actual test - verify camelize behavior
            String result = StringUtils.camelize(testString);
            assertFalse("Result should not contain locale-specific characters", 
                        result.contains(""));
        } finally {
            Locale.setDefault(defaultLocale);
        }
    }

    // Mock StringUtils class to test the exact behavior
    static class StringUtils {
        private static final char ESCAPE_CHAR = '\\';

        public static String camelize(String s) {
            StringBuilder sb = new StringBuilder();
            String[] words = split(s.toLowerCase(Locale.US), ESCAPE_CHAR, '_');
            for (String word : words) {
                sb.append(capitalize(word));
            }
            return sb.toString();
        }

        // Mock implementations of helper methods
        private static String[] split(String s, char escapeChar, char separator) {
            return s.split("_");
        }

        private static String capitalize(String s) {
            if (s == null || s.length() == 0) return s;
            return s.substring(0, 1).toUpperCase() + s.substring(1);
        }
    }
}

