    @Test
    public void testTurkishLocaleCaseConversion() {
        // This test will fail with Locale.US but pass with Locale.ENGLISH
        // because of the Turkish dotted/dotless I behavior
        String input = "TITLE";

        // In Turkish locale, uppercase I becomes lowercase dotless  (U+0131)
        // This would cause different behavior between Locale.US and Locale.ENGLISH
        Locale defaultLocale = Locale.getDefault();
        try {
            // Set Turkish locale to demonstrate the issue
            Locale.setDefault(new Locale("tr", "TR"));

            // The buggy version using Locale.US would be affected by the system locale
            // The fixed version using Locale.ENGLISH would not be affected
            String result = StringUtils.camelize(input);

            // With Locale.ENGLISH, we expect "Title" regardless of system locale
            assertEquals("Title", result);
        } finally {
            // Restore default locale
            Locale.setDefault(defaultLocale);
        }
    }

    // Mock StringUtils class with the camelize method for testing
    static class StringUtils {
        private static final char ESCAPE_CHAR = '\\';

        public static String camelize(String s) {
            StringBuilder sb = new StringBuilder();
            String[] words = split(s.toLowerCase(Locale.US), ESCAPE_CHAR, '_'); // Will fail
            // String[] words = split(s.toLowerCase(Locale.ENGLISH), ESCAPE_CHAR, '_'); // Will pass
            for (String word : words) {
                sb.append(capitalize(word));
            }
            return sb.toString();
        }

        private static String[] split(String s, char escapeChar, char separator) {
            // Simplified split implementation for testing
            return s.split(String.valueOf(separator));
        }

        private static String capitalize(String str) {
            if (str == null || str.length() == 0) {
                return str;
            }
            return str.substring(0, 1).toUpperCase() + str.substring(1);
        }
    }
}

