public class HostCanonicalizationTest {

    @Test
    public void testConcurrentCacheUpdates() throws Exception {
        // Setup test data
        String testHost = "testhost";
        String firstFqdn = "first.example.com";
        String secondFqdn = "second.example.com";

        // Create a mock cache
        ConcurrentHashMap<String, String> cache = new ConcurrentHashMap<>();

        // Simulate concurrent put operations
        String result1 = cache.putIfAbsent(testHost, firstFqdn);
        String result2 = cache.put(testHost, secondFqdn);  // This would overwrite in buggy code

        // In fixed code, the first value should remain
        assertEquals("First putIfAbsent should return null", null, result1);
        assertEquals("Second put should return first value", firstFqdn, result2);
        assertEquals("Cache should retain first value", firstFqdn, cache.get(testHost));

        // Now test with the fixed method
        cache.clear();
        result1 = cache.putIfAbsent(testHost, firstFqdn);
        result2 = cache.putIfAbsent(testHost, secondFqdn);  // This won't overwrite

        assertEquals("First putIfAbsent should return null", null, result1);
        assertEquals("Second putIfAbsent should return first value", firstFqdn, result2);
        assertEquals("Cache should retain first value", firstFqdn, cache.get(testHost));
