import static org.junit.Assert.*;

import java.io.IOException;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

import org.junit.Test;

public class StaticMappingTest {

    // Test class that mimics the original class structure
    static class TestStaticMapping {
        private Object staticMapping;
        private boolean fileExists = false;

        // Buggy version - non-synchronized
        private void initStaticMapping() throws IOException {
            staticMapping = new Object();
            if (fileExists) {
                // Simulate file reading
                Thread.yield();
            }
        }

        // Fixed version - synchronized
        // private synchronized void initStaticMapping() throws IOException {
        //     staticMapping = new Object();
        //     if (fileExists) {
        //         // Simulate file reading
        //         Thread.yield();
        //     }
        // }

        public void concurrentInit() throws InterruptedException {
            final int THREADS = 10;
            ExecutorService es = Executors.newFixedThreadPool(THREADS);
            CountDownLatch latch = new CountDownLatch(THREADS);

            for (int i = 0; i < THREADS; i++) {
                es.execute(() -> {
                    try {
                        initStaticMapping();
                        latch.countDown();
                    } catch (IOException e) {
                        throw new RuntimeException(e);
                    }
                });
            }

            assertTrue("All threads should complete", 
                latch.await(5, TimeUnit.SECONDS));
            es.shutdown();
        }
    }

    @Test
    public void testConcurrentInit() throws InterruptedException {
        TestStaticMapping test = new TestStaticMapping();
        test.fileExists = true; // Force the slow path

        try {
            test.concurrentInit();
            // On buggy version, this may fail due to race conditions
            // On fixed version, this should always pass
        } catch (RuntimeException e) {
            if (e.getCause() instanceof IOException) {
                fail("Unexpected IOException");
            }
            // Allow other runtime exceptions (potential concurrency issues)
            throw e;
        }
    }
}

