import org.junit.Test;
import java.util.Random;
import static org.junit.Assert.*;

public class RandomPositionTest {

    @Test
    public void testRandomPositionNeverNegative() {
        // This test verifies the fix for Math.abs(r.nextLong()) potentially returning negative values
        // when nextLong() returns Long.MIN_VALUE (since abs(Long.MIN_VALUE) is negative)

        Random r = new Random() {
            // Mock to return Long.MIN_VALUE which causes Math.abs() to return negative
            @Override
            public long nextLong() {
                return Long.MIN_VALUE;
            }
        };

        long totalAvailable = 100;

        // With buggy code: Math.abs(Long.MIN_VALUE) returns negative value
        // With fixed code: (Long.MIN_VALUE >>> 1) returns positive value

        long randomPosition = (r.nextLong() >>> 1) % totalAvailable;

        // Assert position is non-negative (main purpose of the fix)
        assertTrue("Random position should not be negative", randomPosition >= 0);

        // Additional check that it's within bounds
        assertTrue("Random position should be less than totalAvailable", 
                  randomPosition < totalAvailable);
    }

    @Test
    public void testRandomPositionDistribution() {
        // Verify the fixed version maintains proper distribution for normal cases
        Random r = new Random(12345L); // fixed seed for reproducibility
        long totalAvailable = 100;
        int iterations = 10000;

        for (int i = 0; i < iterations; i++) {
            long randomPosition = (r.nextLong() >>> 1) % totalAvailable;
            assertTrue("Position should be non-negative", randomPosition >= 0);
            assertTrue("Position should be within bounds", randomPosition < totalAvailable);
        }
    }
}

