import org.apache.hadoop.hdfs.server.blockmanagement.DatanodeStorageInfo;
import org.apache.hadoop.hdfs.server.protocol.DatanodeStorage;
import org.junit.Test;
import static org.junit.Assert.*;
import static org.mockito.Mockito.*;

public class TestStorageRemainingCheck {

    @Test
    public void testIsGoodTargetWithDifferentStorageRemaining() {
        // Setup test data
        final long blockSize = 1024L;
        final long requiredSize = blockSize * 3; // MIN_BLOCKS_FOR_WRITE=3
        final long scheduledSize = blockSize * 2;

        // Create mock objects
        DatanodeStorageInfo storageInfo = mock(DatanodeStorageInfo.class);
        DatanodeStorage storage = mock(DatanodeStorage.class);

        // Configure mocks
        when(storageInfo.getStorage()).thenReturn(storage);
        when(storageInfo.getBlocksScheduled()).thenReturn(2);

        // Case 1: Node has enough space but storage doesn't
        when(storageInfo.getRemaining()).thenReturn(5000L); // node has space
        when(storage.getRemaining()).thenReturn(1000L); // but storage is full

        // Should return false since storage doesn't have space (fixed behavior)
        boolean result = isGoodTarget(storageInfo, blockSize);
        assertFalse("Should reject when storage is full", result);

        // Verify the correct method was called (storage.getRemaining() in fixed code)
        verify(storage, atLeastOnce()).getRemaining();
    }

    // Helper method matching the signature from patch
    private boolean isGoodTarget(DatanodeStorageInfo storage, final long blockSize) {
        final long requiredSize = blockSize * 3; // MIN_BLOCKS_FOR_WRITE
        final long scheduledSize = blockSize * storage.getBlocksScheduled();

        // This is the fixed version - test will fail if buggy version is used
        if (requiredSize > storage.getStorage().getRemaining() - scheduledSize) {
            return false;
        }
        return true;
    }
}

