public class DataNodeRollingUpgradeTest {

    @Mock
    private BlockPoolManager bpos;

    @Mock
    private HeartbeatResponse response;

    @Mock
    private RollingUpgradeStatus rollingStatus;

    private TestDataNode dn;

    @Before
    public void setup() {
        MockitoAnnotations.initMocks(this);
        dn = new TestDataNode(bpos);
    }

    @Test(expected = IOException.class)
    public void testHandleRollingUpgradeStatusPropagatesIOException() throws Exception {
        when(response.getRollingUpdateStatus()).thenReturn(rollingStatus);
        when(rollingStatus.getBlockPoolId()).thenReturn("test-pool");
        when(bpos.getBlockPoolId()).thenReturn("different-pool");

        // Simulate IOException during block pool operations
        doThrow(new IOException("Simulated IO error"))
            .when(bpos).shutdownActor(any());

        dn.handleRollingUpgradeStatus(response);
    }

    // Wrapper class to test protected method
    private static class TestDataNode {
        private final BlockPoolManager bpos;

        public TestDataNode(BlockPoolManager bpos) {
            this.bpos = bpos;
        }

        public void handleRollingUpgradeStatus(HeartbeatResponse resp) throws IOException {
            RollingUpgradeStatus rollingUpgradeStatus = resp.getRollingUpdateStatus();
            if (rollingUpgradeStatus != null &&
                rollingUpgradeStatus.getBlockPoolId().compareTo(bpos.getBlockPoolId()) != 0) {
                bpos.shutdownActor(this);
            }
        }
    }
}

