import static org.junit.Assert.*;
import java.io.IOException;
import java.util.concurrent.atomic.AtomicLong;
import org.junit.Test;

public class DiscardSegmentsTest {
    private final AtomicLong committedTxnId = new AtomicLong();
    private final Object storage = new Object() {
        public Object getJournalManager() {
            return new Object() {
                public void discardSegments(long startTxId) {
                    // Simulate work that could cause race conditions
                    try {
                        Thread.sleep(10);
                    } catch (InterruptedException e) {
                        Thread.currentThread().interrupt();
                    }
                }
            };
        }
    };

    @Test
    public void testDiscardSegmentsThreadSafety() throws InterruptedException {
        final int numThreads = 10;
        Thread[] threads = new Thread[numThreads];

        for (int i = 0; i < numThreads; i++) {
            final long txId = i + 1;
            threads[i] = new Thread(() -> {
                try {
                    discardSegments(txId);
                } catch (IOException e) {
                    fail("Unexpected IOException");
                }
            });
        }

        // Start all threads
        for (Thread t : threads) {
            t.start();
        }

        // Wait for all threads to complete
        for (Thread t : threads) {
            t.join();
        }

        // Verify the committedTxnId was set correctly by last thread
        assertEquals(numThreads - 1, committedTxnId.get());
    }

    // Buggy version (non-synchronized)
    // public void discardSegments(long startTxId) throws IOException {
    // Fixed version (synchronized)
    synchronized void discardSegments(long startTxId) throws IOException {
        storage.getJournalManager().discardSegments(startTxId);
        committedTxnId.set(startTxId - 1);
    }
}

