public class QueueMoveTest {

    @Test
    public void testMoveThreadSafety() throws InterruptedException {
        // Create test objects
        Queue originalQueue = mock(Queue.class);
        Queue newQueue = mock(Queue.class);
        QueueMetrics oldMetrics = mock(QueueMetrics.class);
        QueueMetrics newMetrics = mock(QueueMetrics.class);

        when(originalQueue.getMetrics()).thenReturn(oldMetrics);
        when(newQueue.getMetrics()).thenReturn(newMetrics);

        // Create test instance
        TestableQueueObject testObj = new TestableQueueObject(originalQueue);
        testObj.setUser("testUser");

        // Create and run concurrent threads
        Thread t1 = new Thread(() -> testObj.move(newQueue));
        Thread t2 = new Thread(() -> testObj.move(newQueue));

        t1.start();
        t2.start();
        t1.join();
        t2.join();

        // Verify metrics were transferred exactly once (not twice due to race condition)
        verify(oldMetrics, times(1)).transferStateFromPreviousAttempt(any());
        verify(newMetrics, times(1)).transferStateFromPreviousAttempt(any());
    }

    // Testable class that exposes the move() method
    private static class TestableQueueObject {
        private Queue queue;
        private String user;

        public TestableQueueObject(Queue queue) {
            this.queue = queue;
        }

        public void setUser(String user) {
            this.user = user;
        }

        public String getUser() {
            return user;
        }

        // This is the method we're testing - will be synchronized in fixed version
        public void move(Queue newQueue) {
            QueueMetrics oldMetrics = queue.getMetrics();
            QueueMetrics newMetrics = newQueue.getMetrics();
            String user = getUser();

            oldMetrics.transferStateFromPreviousAttempt(newMetrics);
            newMetrics.transferStateFromPreviousAttempt(oldMetrics);

            this.queue = newQueue;
        }
    }

    // Mock interfaces
    interface Queue {
        QueueMetrics getMetrics();
    }

    interface QueueMetrics {
        void transferStateFromPreviousAttempt(QueueMetrics metrics);
    }
