import org.junit.Test;

public class ClientCheckResponseTest {

    @Test
    public void testCheckResponseErrorMessage() throws Exception {
        byte[] clientId = new byte[]{1, 2, 3};
        byte[] responseId = new byte[]{4, 5, 6};

        RpcResponseHeaderProto header = RpcResponseHeaderProto.newBuilder()
            .setClientId(com.google.protobuf.ByteString.copyFrom(responseId))
            .build();

        try {
            // This should throw IOException with the corrected message
