import org.apache.zookeeper.ZooKeeper;
import org.junit.Test;

public class ActiveStandbyElectorTest {

    @Test
    public void testTerminateConnectionThreadSafety() throws InterruptedException {
        final int THREAD_COUNT = 10;
        final ExecutorService executor = Executors.newFixedThreadPool(THREAD_COUNT);
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch finishLatch = new CountDownLatch(THREAD_COUNT);
        final AtomicInteger successCount = new AtomicInteger(0);

        // Create test instance with mock ZooKeeper client
        ActiveStandbyElector elector = new ActiveStandbyElector() {
            @Override
            protected void createConnection() throws IOException, KeeperException {
                // Mock connection creation
                this.zkClient = mock(ZooKeeper.class);
            }
        };

        try {
            // Initialize connection
            elector.createConnection();

            // Create concurrent termination tasks
            for (int i = 0; i < THREAD_COUNT; i++) {
                executor.submit(() -> {
                    try {
                        startLatch.await();
                        elector.terminateConnection();
                        successCount.incrementAndGet();
                    } finally {
                        finishLatch.countDown();
                    }
                });
            }

            // Start all threads simultaneously
            startLatch.countDown();

            // Wait for completion
            finishLatch.await();

            // Verify all threads completed successfully
            assertEquals("All threads should complete terminateConnection successfully",
                         THREAD_COUNT, successCount.get());

            // Verify connection was terminated
            assertNull("ZooKeeper client should be null after termination", 
                       elector.zkClient);
        } finally {
            executor.shutdown();
        }
    }

    // Mock ZooKeeper class for testing
    private static class ZooKeeper {}
}

