import org.junit.Test;
import java.io.PrintWriter;
import java.io.StringWriter;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class ReflectionUtilsTest {

    @Test
    public void testPrintThreadInfoThreadSafety() throws InterruptedException {
        final int THREAD_COUNT = 10;
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch finishLatch = new CountDownLatch(THREAD_COUNT);
        final StringWriter stringWriter = new StringWriter();
        final PrintWriter printWriter = new PrintWriter(stringWriter);

        ExecutorService executor = Executors.newFixedThreadPool(THREAD_COUNT);

        for (int i = 0; i < THREAD_COUNT; i++) {
            executor.submit(() -> {
                try {
                    startLatch.await();
                    ReflectionUtils.printThreadInfo(printWriter, "Test Title");
                    finishLatch.countDown();
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
            });
        }

        // Start all threads at once to maximize contention
        startLatch.countDown();
        // Wait for all threads to complete
        finishLatch.await();

        // Verify output contains expected content (basic sanity check)
        String output = stringWriter.toString();
        assert(output.contains("Test Title"));
        assert(output.contains("Thread"));

        executor.shutdown();
    }
}

