import org.junit.Test;
import static org.junit.Assert.*;

public class StartInternalTest {

    private volatile boolean methodRunning = false;
    private volatile boolean concurrentAccessDetected = false;

    @Test
    public void testStartInternalSynchronization() throws Exception {
        // Create test object with buggy/fixed implementation
        TestService testService = new TestService();

        // First thread - runs the method
        Thread thread1 = new Thread(() -> {
            try {
                testService.startInternal();
            } catch (Exception e) {
                fail("Unexpected exception");
            }
        });

        // Second thread - checks for concurrent access
        Thread thread2 = new Thread(() -> {
            if (methodRunning) {
                concurrentAccessDetected = true;
            }
        });

        // Start first thread
        thread1.start();

        // Give it a moment to start
        Thread.sleep(50);

        // Start second thread multiple times to increase chance of catching unsynchronized access
        for (int i = 0; i < 10; i++) {
            thread2 = new Thread(() -> {
                if (methodRunning) {
                    concurrentAccessDetected = true;
                }
            });
            thread2.start();
        }

        // Wait for threads to complete
        thread1.join();
        for (int i = 0; i < 10; i++) {
            thread2.join();
        }

        // On buggy code (non-synchronized), concurrentAccessDetected will likely be true
        // On fixed code (synchronized), it should remain false
        assertFalse("Concurrent access detected - method needs synchronization", 
                   concurrentAccessDetected);
    }

    // Test implementation that mimics the real class behavior
    private class TestService {
        protected void startInternal() throws Exception {
            methodRunning = true;
            try {
                // Simulate some work that takes time
                Thread.sleep(100);
            } finally {
                methodRunning = false;
            }
        }

