import static org.junit.Assert.*;
import org.apache.hadoop.hdfs.protocol.DiffReportEntry;
import org.apache.hadoop.hdfs.protocol.DiffType;
import org.apache.hadoop.hdfs.server.namenode.INode;
import org.junit.Test;
import java.util.ArrayList;
import java.util.List;

public class INodeComparisonTest {

    @Test
    public void testGenerateReportWithSameNameDifferentNodes() {
        // Create two INodes with same name but different objects
        byte[] nameBytes = "test".getBytes();
        INode cnode = new TestINode(nameBytes, false);
        INode dnode = new TestINode(nameBytes, true); // different object, same name

        List<INode> created = new ArrayList<>();
        created.add(cnode);
        List<INode> deleted = new ArrayList<>();
        deleted.add(dnode);

        // Create test instance of the class containing generateReport method
        TestDiffGenerator generator = new TestDiffGenerator();

        // This should pass with fixed code (compare names), 
        // but fail with buggy code (equals comparison)
        List<DiffReportEntry> result = generator.generateReport(
            new byte[][]{}, created, deleted);

        // Verify the MODIFY entry was added
        assertEquals(1, result.size());
        assertEquals(DiffType.MODIFY, result.get(0).getType());
    }

    // Helper test classes
    private static class TestDiffGenerator {
        public List<DiffReportEntry> generateReport(byte[][] parentPath, 
                List<INode> created, List<INode> deleted) {
            List<DiffReportEntry> dList = new ArrayList<>();
            int c = 0, d = 0;

            while (c < created.size() && d < deleted.size()) {
                INode cnode = created.get(c);
                INode dnode = deleted.get(d);

                if (cnode.compareTo(dnode.getLocalNameBytes()) == 0) {
                    byte[][] fullPath = new byte[1][];
                    fullPath[0] = cnode.getLocalNameBytes();

                    if (cnode.isSymlink() && dnode.isSymlink()) {
                        dList.add(new DiffReportEntry(DiffType.MODIFY, fullPath));
                    }
                }
                c++;
                d++;
            }
            return dList;
        }
    }

    private static class TestINode extends INode {
        private final byte[] name;
        private final boolean symlink;

        public TestINode(byte[] name, boolean symlink) {
            this.name = name;
            this.symlink = symlink;
        }

        @Override
        public byte[] getLocalNameBytes() {
            return name;
        }

        @Override
        public boolean isSymlink() {
            return symlink;
        }

        @Override
        public int compareTo(byte[] other) {
            return new String(name).compareTo(new String(other));
        }
    }
}

