import org.junit.Test;
import org.apache.hadoop.util.Preconditions;

public class ElementComparisonTest {

    // Mock element class that implements Comparable and has getKey()
    static class TestElement implements Comparable<TestElement> {
        private final String key;

        public TestElement(String key) {
            this.key = key;
        }

        public String getKey() {
            return key;
        }

        @Override
        public int compareTo(TestElement o) {
            return this.key.compareTo(o.getKey());
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (!(obj instanceof TestElement)) return false;
            TestElement other = (TestElement) obj;
            return this.key.equals(other.key);
        }
    }

    @Test
    public void testModifyWithDifferentEqualsButSameKey() {
        // Create elements where equals() would return false but compareTo() returns 0
        TestElement oldElement = new TestElement("key") {
            @Override
            public boolean equals(Object obj) {
                return false; // intentionally broken equals
            }
        };

        TestElement newElement = new TestElement("key");

        try {
            // This should pass in fixed version (compareTo returns 0)
            // but fail in buggy version (equals returns false)
            Preconditions.checkArgument(oldElement.compareTo(newElement.getKey()) == 0,
                "The names do not match: oldElement=%s, newElement=%s",
                oldElement, newElement);
        } catch (IllegalArgumentException e) {
            fail("Should not throw exception when keys match via compareTo");
        }
    }

    @Test(expected = IllegalArgumentException.class)
    public void testModifyWithDifferentKeys() {
        TestElement oldElement = new TestElement("key1");
        TestElement newElement = new TestElement("key2");

        // This should throw in both versions since keys don't match
        Preconditions.checkArgument(oldElement.compareTo(newElement.getKey()) == 0,
            "The names do not match: oldElement=%s, newElement=%s",
            oldElement, newElement);
    }
}

