public class TestSnapshotQuotaUndo {

    @Test
    public void testUndoSnapshotDiskSpaceQuota() throws Exception {
        // Create a mock parent directory that tracks quota changes
        MockINodeDirectory parent = new MockINodeDirectory();

        // Simulate counts that would be passed to addSpaceConsumed
        Quota.Counts counts = Quota.Counts.newInstance();
        counts.set(Quota.NAMESPACE, 5);
        counts.set(Quota.DISKSPACE, 1024L);

        try {
            // This should properly undo the quota consumption
            parent.addSpaceConsumed(-counts.get(Quota.NAMESPACE),
                                   -counts.get(Quota.DISKSPACE), true);

            // Verify the quota was properly reduced
            assertEquals("Namespace quota should be reduced", -5, parent.nsDelta);
            assertEquals("Diskspace quota should be reduced", -1024L, parent.dsDelta);
        } catch (QuotaExceededException e) {
            fail("Should not throw QuotaExceededException when undoing quota");
        }
    }

    // Mock INodeDirectory that tracks quota changes
    private static class MockINodeDirectory extends INodeDirectory {
        public long nsDelta = 0;
        public long dsDelta = 0;

        @Override
        public void addSpaceConsumed(long nsDelta, long dsDelta, boolean verify) 
            throws QuotaExceededException {
            this.nsDelta += nsDelta;
            this.dsDelta += dsDelta;
        }
    }
}

