import org.junit.Test;
import static org.junit.Assert.*;

public class HashCodeTest {

    @Test
    public void testHashCodeCalculation() {
        // Create a test byte array with values that would overflow when cast to int
        byte[] testBytes = new byte[]{(byte) 0x80, (byte) 0xFF}; // -128 and -1 in signed byte

        // Expected hash calculation without the problematic (int) cast:
        // hash = 1
        // First iteration (byte -128): 31*1 + (-128) = -97
        // Second iteration (byte -1): 31*(-97) + (-1) = -3008
        int expectedHash = -3008;

        // Create a test class that mimics the behavior
        TestByteContainer container = new TestByteContainer(testBytes);

        // This will fail on buggy code (due to sign extension from casting byte to int)
        // and pass on fixed code
        assertEquals("Hash calculation should handle byte values correctly", 
                    expectedHash, container.hashCode());
    }

    // Helper class to test the hash calculation behavior
    private static class TestByteContainer {
        private final byte[] bytes;

        public TestByteContainer(byte[] bytes) {
            this.bytes = bytes;
        }

        public byte[] get() {
            return bytes;
        }

        public int hashCode() {
            int hash = 1;
            byte[] b = this.get();
            for (int i = 0; i < b.length; i++) {
                hash = (31 * hash) + b[i]; // This line was patched
            }
            return hash;
        }
    }
}

