    }

    @Test
    public void testStateFieldVolatility() throws InterruptedException {
        TestSubject subject = new TestSubject();
        HAState initialState = new HAState("INITIAL");
        HAState updatedState = new HAState("UPDATED");

        // Test non-volatile field (should fail)
        subject.nonVolatileState = initialState;
        Thread writerThread = new Thread(() -> {
            subject.nonVolatileState = updatedState;
        });

        writerThread.start();
        writerThread.join();

        // This assertion may fail without volatile due to visibility issues
        assertSame("Non-volatile state update not visible", 
                  updatedState, subject.nonVolatileState);

        // Test volatile field (should pass)
        subject.volatileState = initialState;
        writerThread = new Thread(() -> {
            subject.volatileState = updatedState;
        });

        writerThread.start();
        writerThread.join();

        // This should always pass with volatile
        assertSame("Volatile state update should be visible",
                  updatedState, subject.volatileState);
    }

    // Simple mock HAState class
    private static class HAState {
        private final String name;
        public HAState(String name) {
            this.name = name;
        }
    }
}

