import static org.junit.Assert.*;
import static org.mockito.Mockito.*;

import org.apache.hadoop.hdfs.server.blockmanagement.DatanodeDescriptor;
import org.apache.hadoop.hdfs.server.namenode.FSNamesystem;
import org.apache.hadoop.hdfs.server.protocol.DatanodeID;
import org.junit.Before;
import org.junit.Test;

public class BlockReportProcessingTest {
    private FSNamesystem namesystem;
    private DatanodeDescriptor node;
    private BlockManager blockManager;
    private DatanodeID nodeID;

    @Before
    public void setUp() {
        namesystem = mock(FSNamesystem.class);
        node = mock(DatanodeDescriptor.class);
        blockManager = new BlockManager(namesystem);
        nodeID = mock(DatanodeID.class);
    }

    @Test
    public void testProcessReportDuringStartupSafeMode() {
        // Setup mocks
        when(namesystem.isInStartupSafeMode()).thenReturn(true);
        when(node.isFirstBlockReport()).thenReturn(false);
        when(node.numBlocks()).thenReturn(1); // Has blocks but not first report

        // This should be discarded in fixed code but processed in buggy code
        blockManager.processReport(nodeID, "poolId", new long[0], new long[0][0]);

        // Verify behavior changed by patch:
        // In fixed code, firstBlockReport check should prevent processing
        // In buggy code, numBlocks > 0 would allow processing
        verify(node, never()).addBlocks(any(), any());
    }

    @Test
    public void testProcessFirstReportDuringStartupSafeMode() {
        // Setup mocks
        when(namesystem.isInStartupSafeMode()).thenReturn(true);
        when(node.isFirstBlockReport()).thenReturn(true); // First report
        when(node.numBlocks()).thenReturn(1); // Has blocks

        // This should be processed in both versions
        blockManager.processReport(nodeID, "poolId", new long[0], new long[0][0]);

        // Verify first report is always processed
        verify(node).addBlocks(any(), any());
    }

    // Mock BlockManager class for testing
    private static class BlockManager {
        private final FSNamesystem namesystem;

        public BlockManager(FSNamesystem namesystem) {
            this.namesystem = namesystem;
        }

        public void processReport(DatanodeID nodeID, String poolId, 
                                long[] blocks, long[][] blocksWithSize) {
            if (namesystem.isInStartupSafeMode() && !node.isFirstBlockReport()) {
                return; // Discard non-initial reports during startup
            }
            node.addBlocks(blocks, blocksWithSize);
        }
    }
}

