import org.apache.hadoop.fs.Path;
import org.junit.Test;
import java.net.InetSocketAddress;
import java.util.Arrays;
import java.util.List;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;
import static org.junit.Assert.assertTrue;

public class DefaultContainerExecutorTest {
    private static final int THREAD_COUNT = 10;
    private static final int TEST_TIMEOUT_MS = 5000;

    @Test(timeout = TEST_TIMEOUT_MS)
    public void testStartLocalizerThreadSafety() throws Exception {
        DefaultContainerExecutor executor = new DefaultContainerExecutor();
        Path tokensPath = new Path("/tmp/tokens");
        InetSocketAddress nmAddr = new InetSocketAddress("localhost", 8040);
        String user = "testuser";
        String appId = "app_123";
        String locId = "loc_456";
        List<String> localDirs = Arrays.asList("/tmp/local1", "/tmp/local2");
        List<String> logDirs = Arrays.asList("/tmp/logs1", "/tmp/logs2");

        CountDownLatch startLatch = new CountDownLatch(THREAD_COUNT);
        CountDownLatch finishLatch = new CountDownLatch(THREAD_COUNT);
        ExecutorService threadPool = Executors.newFixedThreadPool(THREAD_COUNT);

        for (int i = 0; i < THREAD_COUNT; i++) {
            threadPool.submit(() -> {
                try {
                    startLatch.countDown();
                    startLatch.await();
                    executor.startLocalizer(tokensPath, nmAddr, user, appId, locId, localDirs, logDirs);
                } catch (Exception e) {
                    throw new RuntimeException(e);
                } finally {
                    finishLatch.countDown();
                }
            });
        }

        boolean completed = finishLatch.await(TEST_TIMEOUT_MS, TimeUnit.MILLISECONDS);
        threadPool.shutdownNow();

        assertTrue("All threads should complete without deadlock", completed);
    }
}

