import org.junit.Test;
import static org.junit.Assert.*;

public class FetcherThreadTest {

    @Test(timeout = 5000)
    public void testThreadInterruption() throws InterruptedException {
        // Create and start the fetcher thread
        Thread fetcherThread = new Thread(() -> {
            try {
                while (true && !Thread.currentThread().isInterrupted()) {
                    // Simulate work
                    Thread.sleep(100);
                }
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        fetcherThread.start();

        // Let it run for a bit
        Thread.sleep(200);

        // Interrupt the thread
        fetcherThread.interrupt();

        // Wait for thread to stop
        fetcherThread.join(1000);

        // Verify thread stopped after interruption
        assertFalse("Thread should not be alive after interruption", fetcherThread.isAlive());
    }

    @Test(timeout = 5000)
    public void testThreadInterruptionWithBuggyCode() throws InterruptedException {
        // Create and start the buggy fetcher thread
        Thread buggyThread = new Thread(() -> {
            try {
                while (true) {  // Buggy version doesn't check interruption
                    // Simulate work
                    Thread.sleep(100);
                }
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        buggyThread.start();

        // Let it run for a bit
        Thread.sleep(200);

        // Interrupt the thread
        buggyThread.interrupt();

        // Wait for thread to stop (should timeout since thread keeps running)
        buggyThread.join(1000);

        // This assertion will fail for buggy code, pass for fixed code
        assertFalse("Thread should not be alive after interruption", buggyThread.isAlive());
    }
}

