import static org.junit.Assert.*;
import org.junit.Test;

public class NodeIdTest {

    @Test
    public void testEqualsWithNull() {
        NodeId nodeId = new NodeId("host", 1234);

        // This test will:
        // - FAIL on buggy code (throws NPE or returns wrong result due to super.equals(null) call)
        // - PASS on fixed code (properly handles null comparison)
        assertFalse(nodeId.equals(null));
    }

    // Minimal NodeId class implementation for test compilation
    static class NodeId {
        private final String host;
        private final int port;

        public NodeId(String host, int port) {
            this.host = host;
            this.port = port;
        }

        // Buggy version
        /*
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (!super.equals(obj)) return false;
            if (getClass() != obj.getClass()) return false;
            NodeId other = (NodeId) obj;
            return port == other.port && host.equals(other.host);
        }
        */

        // Fixed version
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (obj == null) return false;
            if (getClass() != obj.getClass()) return false;
            NodeId other = (NodeId) obj;
            return port == other.port && host.equals(other.host);
        }

        public int hashCode() {
            return host.hashCode() + port;
        }
    }
}

