import static org.junit.Assert.*;
import org.junit.Test;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicInteger;
import org.apache.hadoop.yarn.api.records.ApplicationId;
import org.apache.hadoop.yarn.api.records.ApplicationSubmissionContext;
import org.apache.hadoop.yarn.server.resourcemanager.RMContext;
import org.apache.hadoop.yarn.server.resourcemanager.rmapp.RMApp;

public class RMAppManagerTest {
    private static final int THREAD_COUNT = 10;

    // Mock class to test the synchronization behavior
    class TestRMAppManager extends RMAppManager {
        final AtomicInteger concurrentSubmissions = new AtomicInteger(0);
        final CountDownLatch latch = new CountDownLatch(1);

        public TestRMAppManager(RMContext context) {
            super(context);
        }

        @Override
        protected void submitApplication(ApplicationSubmissionContext submissionContext) {
            // Track concurrent submissions
            int current = concurrentSubmissions.incrementAndGet();
            if (current > 1) {
                fail("Multiple concurrent submissions detected");
            }

            try {
                latch.await(); // Block to allow other threads to enter
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }

            concurrentSubmissions.decrementAndGet();
        }
    }

    @Test
    public void testSubmitApplicationSynchronization() throws InterruptedException {
        TestRMAppManager manager = new TestRMAppManager(null);
        Thread[] threads = new Thread[THREAD_COUNT];

        // Start multiple threads trying to submit applications
        for (int i = 0; i < THREAD_COUNT; i++) {
            threads[i] = new Thread(() -> {
                manager.submitApplication(new MockApplicationSubmissionContext());
            });
            threads[i].start();
        }

        // Give threads time to hit the synchronization point
        Thread.sleep(100);

        // Release all threads
        manager.latch.countDown();

        // Wait for all threads to complete
        for (Thread t : threads) {
            t.join();
        }

        // If we get here without failing in the submitApplication method,
        // the synchronization worked correctly
    }

    // Mock ApplicationSubmissionContext implementation
    private static class MockApplicationSubmissionContext extends ApplicationSubmissionContext {
        @Override
        public ApplicationId getApplicationId() {
            return null;
        }
    }
}

