public class FileContextSingletonTest {

    @Test
    public void testSingletonInitializationThreadSafety() throws Exception {
        final int threadCount = 10;
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch finishLatch = new CountDownLatch(threadCount);
        final AtomicReference<Throwable> error = new AtomicReference<>();

        // Reset the singleton for testing
        resetSingleton();

        Runnable task = () -> {
            try {
                startLatch.await();
                FileContext.getFileContext();
            } catch (Throwable t) {
                error.set(t);
            } finally {
                finishLatch.countDown();
            }
        };

        // Start multiple threads
        for (int i = 0; i < threadCount; i++) {
            new Thread(task).start();
        }

        // Trigger all threads to start simultaneously
        startLatch.countDown();
        finishLatch.await();

        if (error.get() != null) {
            throw new AssertionError("Thread failed", error.get());
        }

        // Verify singleton was properly initialized
        assertNotNull(FileContext.getFileContext());
    }

    // Helper method to reset the singleton for testing
    private void resetSingleton() throws Exception {
        // Use reflection to reset the singleton field
        java.lang.reflect.Field field = FileContext.class.getDeclaredField("localFsSingleton");
        field.setAccessible(true);
        field.set(null, null);
    }
}

