import org.junit.Test;
import static org.junit.Assert.*;

import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicReference;

public class ReflectionUtilsTest {

    @Test
    public void testSerialFactoryThreadSafety() throws InterruptedException {
        final int threadCount = 10;
        final CountDownLatch startLatch = new CountDownLatch(1);
        final CountDownLatch endLatch = new CountDownLatch(threadCount);
        final AtomicReference<Exception> error = new AtomicReference<>();

        // Reset the field for test
        ReflectionUtils.serialFactory = null;

        Runnable task = () -> {
            try {
                startLatch.await();
                ReflectionUtils.serialFactory = new SerializationFactory();
            } catch (Exception e) {
                error.set(e);
            } finally {
                endLatch.countDown();
            }
        };

        // Start all threads
        for (int i = 0; i < threadCount; i++) {
            new Thread(task).start();
        }

        // Trigger all threads simultaneously
        startLatch.countDown();

        // Wait for all threads to complete
        endLatch.await();

        // Verify no errors occurred and field was set exactly once
        assertNull("Thread safety error occurred", error.get());
        assertNotNull("serialFactory was not initialized", ReflectionUtils.serialFactory);
    }

    // Mock SerializationFactory for testing
    static class SerializationFactory {}

    // Mock ReflectionUtils with the field we want to test
    static class ReflectionUtils {
        volatile static SerializationFactory serialFactory = null;
    }
}

