import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import org.apache.hadoop.fs.FileSystem;
import org.apache.hadoop.fs.Path;
import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.Mock;
import org.mockito.Mockito;
import org.mockito.junit.MockitoJUnitRunner;
import org.slf4j.Logger;

@RunWith(MockitoJUnitRunner.class)
public class TrashTest {
    @Mock
    private FileSystem fs;

    @Mock
    private Logger logger;

    private TestableTrash trash;

    private static final Path BASE_TRASH_PATH = new Path("/user/.Trash");
    private static final Path PATH_TO_DELETE = new Path("/user/file.txt");

    class TestableTrash extends org.apache.hadoop.fs.Trash {
        public TestableTrash(FileSystem fs, Path home) {
            super(fs, home);
        }

        @Override
        protected Logger getLog() {
            return logger;
        }
    }

    @Before
    public void setup() {
        trash = new TestableTrash(fs, new Path("/user"));
    }

    @Test
    public void testMoveToTrashLogsCorrectMessageWhenMkdirFails() throws Exception {
        // Setup mock to fail mkdirs
        when(fs.mkdirs(BASE_TRASH_PATH, Mockito.any())).thenReturn(false);

        // Execute the method
        trash.moveToTrash(PATH_TO_DELETE);

        // Verify the exact log message was called
        verify(logger).warn("Can't create(mkdir) trash directory: " + BASE_TRASH_PATH);
    }
}

