import org.apache.hadoop.conf.Configuration;
import org.junit.Test;
import static org.junit.Assert.*;

public class GenericOptionParserTest {

    @Test
    public void testProcessGeneralOptionsWithEqualsInValue() {
        Configuration conf = new Configuration();
        GenericOptionParser parser = new GenericOptionParser();

        // Test case with '=' in the value part
        String propertyWithEquals = "key=value=with=equals";

        // This would fail in buggy version (splitting into multiple parts)
        // but passes in fixed version (splitting into exactly 2 parts)
        String[] keyval = propertyWithEquals.split("=", 2);
        assertEquals(2, keyval.length);
        assertEquals("key", keyval[0]);
        assertEquals("value=with=equals", keyval[1]);

        // Test the actual method behavior
        // In buggy version, this would split into ["key", "value", "with", "equals"]
        // causing ArrayIndexOutOfBoundsException when accessing keyval[1]
        parser.processGeneralOptions(conf, new String[]{"-D" + propertyWithEquals});

        // Verify the value was properly set
        assertEquals("value=with=equals", conf.get("key"));
    }

    // Mock/stub version of the class under test
    private static class GenericOptionParser {
        public void processGeneralOptions(Configuration conf, String[] args) {
            for (String arg : args) {
                if (arg.startsWith("-D")) {
                    String prop = arg.substring(2);
                    String[] keyval = prop.split("=", 2);  // This is the patched line
                    if (keyval.length == 2) {
                        conf.set(keyval[0], keyval[1]);
                    }
                }
            }
        }
    }
}

